                            Cancel
                            </AddItemButtons >
                        </div>
                        <div className='addTask addTaskButtonBox'>
                            <AddItemButtons inverse >
                            Add Task
                            </AddItemButtons>
                        </div>
                    </div>


                </form>
            </div>

    )

}


export default AddItemModal;
