import React, { Component }  from 'react';
import ReactDOM from 'react-dom';
import { CSSTransition } from 'react-transition-group';

import Backdrop from './Backdrop';
import './LoginModal.css';

const LoginModalOverlay = props => {
    const content = (
      <div className='loginModalOverlay'>
       <h1>Login</h1>
        <form
        //   onSubmit={
        //     props.onSubmit ? props.onSubmit : event => event.preventDefault()
        //   }
        >
         <div className='field field_email'>   
            <label class='label' for='email'>Email</label>   
            <input type='email' name='email' class='input input_email'/>
        </div>
        <div className='field field_password'>   
            <label class='label' for='password'>Password</label>   
            <input type='email' name='email' class='input input_email'/>
        </div>

        <button onclick='' className='loginSubmitButton'>Login</button>
        </form>
      </div>
    );
    return ReactDOM.createPortal(content, document.getElementById('modal-hook'));
  };


const LoginModal = props => {
    return (
      <React.Fragment>
        <Backdrop onClick={props.onCancel} />
        {/* <CSSTransition
          in={props.show}
          mountOnEnter
          unmountOnExit
          timeout={200}
          classNames="loginModal"
        >
          <LoginModalOverlay {...props} />
        </CSSTransition> */}
      </React.Fragment>
    );
  };

export default LoginModal;
