import React, { useState, useContext, useEffect }  from 'react';
import TagBar from './TagBar/TagBar';
import List from './List/List';
import { AuthContext } from '../../context/AuthContext';


import './MainPage.css';

const MainPage = () => {

  const auth = useContext(AuthContext);
  const userId = auth.userId;
  console.log(userId)


  const [loadedItems, setLoadedItems] = useState();
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState();

  useEffect(() => {

    const fetchItems = async () => {


      setIsLoading(true);

        try{
            const response = await fetch(`http://localhost:5000/api/items/${userId}`, 
            {
              method: 'GET', 
              headers: {
                'Content-Type': 'application/json'
              }, 
              Authorization: "Bearer " + auth.token
            })
            const responseData = await response.json();
            console.log(responseData)
            setLoadedItems(responseData.items)

            if (!response.ok) {
              throw new Error(responseData.message);
            }
            setIsLoading(false)



        }catch(err){
          console.log(err)
          setIsLoading(false);
          setError(err.message || 'Something went wrong, please try again');

          }
        }

       fetchItems();


  },  [])



    return (
      <div className='mainContainerOuterBox'>

      {!isLoading && loadedItems  && (
         <div className='mainContainer'>
              {/* <div className='TagBarContainer'>
                <TagBar className = 'TagBar' items = {loadedItems} />
              </div> */}
              <div className='ListContainer'>
                  <List className = 'List' items = {loadedItems} />
              </div>
              </div>)    } 
           </div>


    );
  }

  export default MainPage;
