import React, { useState, useContext, useEffect }  from 'react';
import TagBar from './TagBar/TagBar';
import List from './List/List';
import { AuthContext } from '../../../context/AuthContext';


import './MainPage.css';

const MainPage = () => {

  const auth = useContext(AuthContext);
  const userId = auth.userId;
  const token = auth.token

  const [loadedItems, setLoadedItems] = useState();

  useEffect(() => {

  const fetchItems = async () => {
  try{
      const response = await fetch(`http://localhost:5000/api/items/${userId}`, {
        method: 'GET', 
        headers: {
          'Content-Type': 'application/json'
        }

      })

      const responseData= await response.json();
      console.log(responseData)
      setLoadedItems(responseData.items)


    }catch(err){
      console.log(err)
      // setError(err.message || 'Something went wrong, please try again')

    }
      }
  }, userId)


    const dummyList =[
        {id:'01', 
        tag:'important', 
        content:'technical assignment', 
        date:'2020-10-13',
        completed:false,

        },
        {id:'02', 
        tag:'remote', 
        content:'assignment2', 
        date:'2020-10-13',
        completed:true,

        },

    ];



    return (
      <div className='mainContainer'>
          <div className='TagBarContainer'>
             <TagBar className = 'TagBar'/>
          </div>
          <div className='ListContainer'>
             <List className = 'List' items = {loadedItems} />
          </div>
      </div>

    );
  }

  export default MainPage;
