import React, { useState, useContext, useEffect }  from 'react';

import { AuthContext } from '../../context/AuthContext';
import { useHttpClient } from "../../hooks/http-hook";
import { SearchContext } from '../../context/SearchContext';
import List from './List/List';
import './MainPage.css'

const SearchResult = (props) => {

    const { isLoading, error, sendRequest, clearError } = useHttpClient();

    const auth = useContext(AuthContext);
    const userId = auth.userId;
    console.log(userId)

    const [searchResult, setSearchResult] = useState([]);
    const searchInput = props.searchInput;
    console.log(searchInput)

    const itemDeleteHandler = (deletedItemId) =>{
      setSearchResult((prevItems)=>{
        prevItems.filter(i => i._id !== deletedItemId)
      })
    }

    useEffect(() => {
        const searchItems = async () => {

               try {
                  const responseData = await sendRequest(
                    `http://localhost:5000/api/items/${userId}/search`, 
                    'GET',
                    {
                       body: JSON.stringify({
                        keyword: searchInput })
                    },
                    {
                      Authorization: 'Bearer ' + auth.token,
                    }
                  );
                  setSearchResult(responseData.items);
                  console.log(responseData)
                  console.log(searchResult)
                } catch (err) {}
          };

            searchItems()

          }, [sendRequest, searchInput]);

    return(
      <div className='mainContainerOuterBox'>
      // <div className='searchListContainer'>
      {/* //     {searchResult && ( */}
            <List className = 'List' items = {searchResult} onDeleteItem = {itemDeleteHandler}/>
          {/* )} */}
       </div>
      </div>

    )



}  






export default SearchResult;
