import React, { useState, useContext } from 'react';
import './NavBar.css';
import logo2 from '../images/logo2.png';

import {
    BrowserRouter as Router,
    Route,
    Redirect,
    Switch,
    NavLink
  } from "react-router-dom";

import AddItem from './AddItem';
import SearchBar from './SearchBar';
import LoginButton from './LoginButton';
import RegisterButton from './RegisterButton';
import AddItemModal from '../modals/AddItemModal'
import Backdrop from '../modals/Backdrop'
import LoginModal from '../modals/LoginModal';
import RegisterModal from '../modals/RegisterModal';
import { AuthContext } from '../../context/AuthContext';




const NavBar = () =>{
    const auth = useContext(AuthContext);

    const [addItemModalOpen, setAddItemModalOpen] =useState(false);
    const [loginModalOpen, setLoginModalOpen] =useState(false);
    const [registerModalOpen, setRegisterModalOpen] =useState(false);

    const addItemHandler = () =>{
        setAddItemModalOpen(true)
    }

    const loginHandler = () => {
        setLoginModalOpen(true);
    }

    const registerHandler = () => {
        setRegisterModalOpen(true);
    }

    const onCancel = () =>{
        setAddItemModalOpen(false);
        setLoginModalOpen(false);
        setRegisterModalOpen(false);
    }

    const closeLoginModel = () =>{
        setLoginModalOpen(false)
    }



    return(
        <React.Fragment>
            {addItemModalOpen && <Backdrop onClick={onCancel} />}
            {addItemModalOpen && <AddItemModal  />}

            {loginModalOpen && <Backdrop onClick={onCancel} />}
            {loginModalOpen && <LoginModal switch={closeLoginModel} />}


            {registerModalOpen && <Backdrop onClick={onCancel} />}
            {registerModalOpen && <RegisterModal onClick={onCancel} />}

            <div className="headerBox">
                <div className='logoBox'>
                    <img className="logo2" src={logo2} alt='logo_TV2Z' />
                </div>
                <div className ="searchBarContainer">
                    <SearchBar />
                </div>
                <div className ="addItemContainer">
                    <AddItem onClick={addItemHandler} />
                </div>
                {!auth.isLoggedIn && (
                <div className ="loginButtonContainer">
                    <LoginButton onClick={loginHandler} />
                </div>
                )}
                 {!auth.isLoggedIn && (
                <div className ="registerButtonContainer">
                    <RegisterButton onClick={registerHandler} />
                </div>
                 )}
                 {auth.isLoggedIn && (
                <div className ="logoutButtonContainer">
                    <button onClick = {auth.logout}>LOG OUT</button>
                </div>
                 )}


            </div>
      </React.Fragment>
    )
}

export default NavBar;
