import React, { useState, useContext, useEffect }  from 'react';
import TagBar from './TagBar/TagBar';
import List from './List/List';
import { AuthContext } from '../../context/AuthContext';
import axios from "../axios";


import './MainPage.css';

const MainPage = () => {

  const auth = useContext(AuthContext);
  const userId = auth.userId;
  const token = auth.token;


  const [loadedItems, setLoadedItems] = useState();

  useEffect(() => {

    const fetchItems = async () => {

      const response = await axios.get(`http://localhost:5000/api/items/${userId}`);
      const responseData = await response.data;
      console.log(responseData)
      setLoadedItems(responseData.items)
        // try{
        //     const response = await fetch(`http://localhost:5000/api/items/${userId}`, {method: 'GET'})
        //     const responseData = await response.json();
        //     return responseData;
        // }catch(err){
        //   console.log(err)
        //   // setError(err.message || 'Something went wrong, please try again')
        //   }
        }
      fetchItems();

  },  [])



    return (
      <div className='mainContainer'>
          <div className='TagBarContainer'>
             <TagBar className = 'TagBar'/>
          </div>
          {
            loadedItems && (
              <div className='ListContainer'>
              <List className = 'List' items = {loadedItems} />
           </div>

            )
          }

      </div>

    );
  }

  export default MainPage;
